package com.arcus.javatraining.operators;

public class PostPrefixOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		System.out.println(i++);//returns original i and then increments
		System.out.println(i);
		System.out.println(++i);
		
		System.out.println(--i);
		
		System.out.println(5+6+"sud");
		System.out.println("sud"+5+6);
		

	}

}
